function import_terms_tree($items, $tax_name, $parent_term = null) {
	foreach ($items as $item) {
		$inserted_item = wp_insert_term(
			$item['title'],
			$tax_name,
			[
				'slug' => $item['slug'],
				'parent' => $parent_term ?: $item['parent']
			]
		);
		if (isset($inserted_item['term_id'])) {
			update_term_meta($inserted_item['term_id'], 'content', get_term_meta_content($item['slug']));
			if (isset($item['childs'])) {
				import_terms_tree($item['childs'], $tax_name, $inserted_item['term_id']);
			}
		}
	}
}
